import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function CourseInsights() {
  const { id } = useParams(); // get course id from URL
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInsights = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`/api/ai/course-insights/${id}`);
        if (res.data.success) {
          setInsights(res.data.insights);
        } else {
          setError(res.data.error || "Failed to fetch course insights.");
        }
      } catch (err) {
        setError("Error fetching course insights.");
      } finally {
        setLoading(false);
      }
    };

    fetchInsights();
  }, [id]);

  if (loading) return <p className="text-gray-600">‚è≥ Loading course insights...</p>;
  if (error) return <p className="text-red-600">‚ö†Ô∏è {error}</p>;

  return (
    <div className="p-6 bg-gray-50 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4">üìò Course Insights</h2>

      {insights ? (
        <div className="bg-white p-5 rounded-lg shadow">
          <p className="mb-2"><strong>üìä Difficulty:</strong> {insights.difficulty_level}</p>
          <p className="mb-2"><strong>üõ† Key Skills:</strong> {insights.key_skills.join(", ")}</p>
          <p className="mb-2"><strong>üíº Career Path:</strong> {insights.career_path}</p>
          <p className="mb-2"><strong>üìà Demand:</strong> {insights.demand}</p>
        </div>
      ) : (
        <p className="text-gray-600">No insights available for this course.</p>
      )}
    </div>
  );
}
