import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AIRecommendations() {
  const [recommendations, setRecommendations] = useState([]);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [enrolling, setEnrolling] = useState(null);

  // Fetch recommendations when component loads
  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        setLoading(true);
        const res = await axios.get("/api/ai/recommendations");
        if (res.data.success) {
          setRecommendations(res.data.recommendations);
          setProfile(res.data.profile);
        } else {
          setError(res.data.error || "Failed to fetch recommendations.");
        }
      } catch (err) {
        setError("Error loading recommendations.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, []);

  // Handle enrolling in a course
  const handleEnroll = async (courseId) => {
    try {
      setEnrolling(courseId);
      const res = await axios.post("/api/student/enroll-course", {
        course_id: courseId,
      });

      if (res.data.success) {
        alert("✅ Enrolled successfully!");
      } else {
        alert("❌ Enrollment failed: " + (res.data.error || "Unknown error"));
      }
    } catch (err) {
      alert("❌ Enrollment request failed.");
    } finally {
      setEnrolling(null);
    }
  };

  if (loading) return <p className="text-gray-600">⏳ Loading recommendations...</p>;
  if (error) return <p className="text-red-600">⚠️ {error}</p>;

  return (
    <div className="p-6 bg-gray-50 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4">🎯 AI Recommendations</h2>

      {/* Profile Section */}
      {profile && (
        <div className="bg-white p-4 mb-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-2">📊 Your Learning Profile</h3>
          <p><strong>Skill Level:</strong> {profile.skill_level}</p>
          <p><strong>Completion Rate:</strong> {profile.completion_rate * 100}%</p>
          <p><strong>Total Courses Taken:</strong> {profile.total_courses_taken}</p>
        </div>
      )}

      {/* Recommendations Section */}
      <h3 className="text-xl font-semibold mb-3">📚 Recommended Courses</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {recommendations.length > 0 ? (
          recommendations.map((course) => (
            <div key={course.id} className="bg-white p-4 rounded-lg shadow hover:shadow-lg transition">
              <h4 className="font-bold text-lg mb-1">{course.title}</h4>
              <p className="text-sm text-gray-600 mb-2">{course.description}</p>
              <button
                onClick={() => handleEnroll(course.id)}
                className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
                disabled={enrolling === course.id}
              >
                {enrolling === course.id ? "⏳ Enrolling..." : "Enroll"}
              </button>
            </div>
          ))
        ) : (
          <p className="text-gray-600">No recommendations available.</p>
        )}
      </div>
    </div>
  );
}
