import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Feedback from './Feedback';
import './FeedbackDashboard.css';

const FeedbackDashboard = ({ student }) => {
  const [analytics, setAnalytics] = useState(null);
  const [recentFeedback, setRecentFeedback] = useState([]);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [courseLessons, setCourseLessons] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [feedbackTarget, setFeedbackTarget] = useState(null);
  const [selectedFeedback, setSelectedFeedback] = useState(null);

  useEffect(() => {
    if (student) {
      fetchFeedbackAnalytics();
      fetchRecentFeedback();
      fetchEnrolledCourses();
    }
  }, [student]);

  const fetchFeedbackAnalytics = async () => {
    try {
      const response = await axios.get('/api/student/feedback-analytics');
      if (response.data.success) {
        setAnalytics(response.data.analytics);
      }
    } catch (error) {
      console.error('Error fetching feedback analytics:', error);
      setError('Failed to load feedback analytics');
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentFeedback = async () => {
    try {
      const response = await axios.get('/api/student/feedback');
      if (response.data.success) {
        setRecentFeedback(response.data.feedback);
      }
    } catch (error) {
      console.error('Error fetching recent feedback:', error);
    }
  };

  const fetchEnrolledCourses = async () => {
    try {
      const response = await axios.get('/api/student/enrolled-courses');
      if (response.data.success) {
        setEnrolledCourses(response.data.courses);
        // Fetch lessons for each course
        response.data.courses.forEach(course => {
          fetchCourseLessons(course.id);
        });
      }
    } catch (error) {
      console.error('Error fetching enrolled courses:', error);
    }
  };

  const fetchCourseLessons = async (courseId) => {
    try {
      const response = await axios.get(`/api/courses/${courseId}/lessons`);
      if (response.data.success) {
        setCourseLessons(prev => ({
          ...prev,
          [courseId]: response.data.lessons
        }));
      }
    } catch (error) {
      console.error(`Error fetching lessons for course ${courseId}:`, error);
    }
  };

  const handleDeleteFeedback = async (feedbackId) => {
    if (!window.confirm('Are you sure you want to delete this feedback?')) {
      return;
    }

    try {
      const response = await axios.delete('/api/student/feedback', {
        data: { feedback_id: feedbackId }
      });

      if (response.data.success) {
        fetchFeedbackAnalytics();
        fetchRecentFeedback();
        alert('Feedback deleted successfully');
      }
    } catch (error) {
      console.error('Error deleting feedback:', error);
      alert('Failed to delete feedback');
    }
  };

  const renderStars = (rating) => {
    return (
      <div className="stars-display">
        {[1, 2, 3, 4, 5].map((star) => (
          <i 
            key={star} 
            className={`fas fa-star ${star <= rating ? 'filled' : ''}`}
          ></i>
        ))}
      </div>
    );
  };

  const getRatingColor = (rating) => {
    if (rating >= 4) return '#28a745';
    if (rating >= 3) return '#ffc107';
    return '#dc3545';
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'content': return 'fas fa-book';
      case 'difficulty': return 'fas fa-signal';
      case 'instructor': return 'fas fa-chalkboard-teacher';
      case 'technical': return 'fas fa-tools';
      default: return 'fas fa-comment';
    }
  };

  const getCategoryColor = (category) => {
    switch (category) {
      case 'content': return '#667eea';
      case 'difficulty': return '#fd7e14';
      case 'instructor': return '#28a745';
      case 'technical': return '#dc3545';
      default: return '#6c757d';
    }
  };

  const getFeedbackForTarget = (targetType, targetId) => {
    return recentFeedback.filter(f => 
      f.feedback_type === targetType && f.target_id === targetId
    );
  };

  if (loading) {
    return (
      <div className="feedback-dashboard">
        <div className="loading-container">
          <div className="loading-spinner">
            <i className="fas fa-spinner fa-spin"></i>
            <p>Loading feedback analytics...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feedback-dashboard">
        <div className="error-container">
          <i className="fas fa-exclamation-triangle"></i>
          <p>{error}</p>
          <button onClick={fetchFeedbackAnalytics} className="retry-btn">
            <i className="fas fa-redo"></i>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="feedback-dashboard">
      <div className="dashboard-header">
        <h2>
          <i className="fas fa-comments"></i>
          Feedback Dashboard
        </h2>
        <p>Track and manage your course and lesson feedback</p>
      </div>

      {/* Analytics Overview */}
      {analytics && analytics.overall_stats ? (
        <div className="analytics-overview">
          <div className="analytics-grid">
            <div className="analytics-card">
              <div className="analytics-icon">
                <i className="fas fa-comment-alt"></i>
              </div>
              <div className="analytics-content">
                <h3>{analytics.overall_stats.total_feedback || 0}</h3>
                <p>Total Feedback</p>
              </div>
            </div>
            
            <div className="analytics-card">
              <div className="analytics-icon rating">
                <i className="fas fa-star"></i>
              </div>
              <div className="analytics-content">
                <h3>{analytics.overall_stats.avg_rating ? (parseFloat(analytics.overall_stats.avg_rating) || 0).toFixed(1) : '0.0'}</h3>
                <p>Average Rating</p>
              </div>
            </div>
            
            <div className="analytics-card">
              <div className="analytics-icon positive">
                <i className="fas fa-thumbs-up"></i>
              </div>
              <div className="analytics-content">
                <h3>{analytics.overall_stats.positive_feedback || 0}</h3>
                <p>Positive Reviews</p>
              </div>
            </div>
            
            <div className="analytics-card">
              <div className="analytics-icon negative">
                <i className="fas fa-thumbs-down"></i>
              </div>
              <div className="analytics-content">
                <h3>{analytics.overall_stats.negative_feedback || 0}</h3>
                <p>Negative Reviews</p>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="no-analytics">
          <i className="fas fa-chart-bar"></i>
          <p>No feedback analytics available yet. Submit your first feedback to see statistics!</p>
        </div>
      )}

      {/* Category Breakdown */}
      {analytics && analytics.category_stats && analytics.category_stats.length > 0 ? (
        <div className="category-breakdown">
          <h3>Feedback by Category</h3>
          <div className="category-grid">
            {analytics.category_stats.map((category) => (
              <div key={category.category} className="category-card">
                <div className="category-header">
                  <i 
                    className={getCategoryIcon(category.category)}
                    style={{ color: getCategoryColor(category.category) }}
                  ></i>
                  <span className="category-name">{category.category}</span>
                </div>
                <div className="category-stats">
                  <div className="stat">
                    <span className="stat-value">{category.count}</span>
                    <span className="stat-label">Reviews</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{category.avg_rating ? (parseFloat(category.avg_rating) || 0).toFixed(1) : '0.0'}</span>
                    <span className="stat-label">Avg Rating</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : null}

      {/* Enrolled Courses with Feedback */}
      <div className="enrolled-courses-feedback">
        <h3>Your Enrolled Courses</h3>
        <p>Submit feedback for each course to help improve the learning experience</p>
        
        {enrolledCourses.length === 0 ? (
          <div className="no-courses">
            <i className="fas fa-book-open"></i>
            <p>You haven't enrolled in any courses yet.</p>
          </div>
        ) : (
          <div className="courses-grid">
            {enrolledCourses.map((course) => {
              const courseFeedback = getFeedbackForTarget('course', course.id);
              const lessons = courseLessons[course.id] || [];
              return (
                <div key={course.id} className="course-feedback-card">
                  <div className="course-header">
                    <h4>{course.course_name}</h4>
                    <span className="course-level">{course.level}</span>
                  </div>
                  
                  <div className="course-description">
                    <p>{course.description}</p>
                  </div>
                  
                  {/* Course Feedback Section */}
                  <div className="course-feedback-section">
                    <h5>Course Feedback:</h5>
                    {courseFeedback.length > 0 ? (
                      <div className="existing-feedback">
                        {courseFeedback.map((feedback) => (
                          <div key={feedback.id} className="feedback-summary">
                            <div className="feedback-rating">
                              {renderStars(feedback.rating)}
                              <span className="rating-text">{feedback.rating}/5</span>
                            </div>
                            <div className="feedback-category">
                              <i className={getCategoryIcon(feedback.category)}></i>
                              <span>{feedback.category}</span>
                            </div>
                            {feedback.comment && (
                              <p className="feedback-comment">{feedback.comment}</p>
                            )}
                            <div className="feedback-actions">
                              <button 
                                className="edit-btn small"
                                onClick={() => {
                                  setSelectedFeedback(feedback);
                                  setFeedbackTarget({
                                    type: 'course',
                                    id: course.id,
                                    name: course.course_name
                                  });
                                  setShowFeedback(true);
                                }}
                              >
                                <i className="fas fa-edit"></i>
                                Edit
                              </button>
                              <button 
                                className="delete-btn small"
                                onClick={() => handleDeleteFeedback(feedback.id)}
                              >
                                <i className="fas fa-trash"></i>
                                Delete
                              </button>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="no-feedback-yet">
                        <p>No course feedback submitted yet</p>
                      </div>
                    )}
                    
                    <button 
                      className="add-feedback-btn course-feedback-btn"
                      onClick={() => {
                        setFeedbackTarget({
                          type: 'course',
                          id: course.id,
                          name: course.course_name
                        });
                        setShowFeedback(true);
                      }}
                    >
                      <i className="fas fa-plus"></i>
                      {courseFeedback.length > 0 ? 'Add More Course Feedback' : 'Submit Course Feedback'}
                    </button>
                  </div>

                  {/* Lessons Feedback Section */}
                  {lessons.length > 0 && (
                    <div className="lessons-feedback-section">
                      <h5>Lesson Feedback:</h5>
                      <div className="lessons-grid">
                        {lessons.map((lesson) => {
                          const lessonFeedback = getFeedbackForTarget('lesson', lesson.id);
                          return (
                            <div key={lesson.id} className="lesson-feedback-item">
                              <div className="lesson-header">
                                <h6>{lesson.lesson_name}</h6>
                                <span className="lesson-module">{lesson.module_name}</span>
                              </div>
                              
                              <div className="lesson-feedback-content">
                                {lessonFeedback.length > 0 ? (
                                  <div className="lesson-feedback-summary">
                                    <div className="feedback-rating">
                                      {renderStars(lessonFeedback[0].rating)}
                                      <span className="rating-text">{lessonFeedback[0].rating}/5</span>
                                    </div>
                                    <div className="feedback-category">
                                      <i className={getCategoryIcon(lessonFeedback[0].category)}></i>
                                      <span>{lessonFeedback[0].category}</span>
                                    </div>
                                    {lessonFeedback[0].comment && (
                                      <p className="feedback-comment">{lessonFeedback[0].comment}</p>
                                    )}
                                    <div className="feedback-actions">
                                      <button 
                                        className="edit-btn small"
                                        onClick={() => {
                                          setSelectedFeedback(lessonFeedback[0]);
                                          setFeedbackTarget({
                                            type: 'lesson',
                                            id: lesson.id,
                                            name: lesson.lesson_name
                                          });
                                          setShowFeedback(true);
                                        }}
                                      >
                                        <i className="fas fa-edit"></i>
                                        Edit
                                      </button>
                                      <button 
                                        className="delete-btn small"
                                        onClick={() => handleDeleteFeedback(lessonFeedback[0].id)}
                                      >
                                        <i className="fas fa-trash"></i>
                                        Delete
                                      </button>
                                    </div>
                                  </div>
                                ) : (
                                  <div className="no-lesson-feedback">
                                    <p>No feedback yet</p>
                                    <button 
                                      className="add-feedback-btn lesson-feedback-btn"
                                      onClick={() => {
                                        setFeedbackTarget({
                                          type: 'lesson',
                                          id: lesson.id,
                                          name: lesson.lesson_name
                                        });
                                        setShowFeedback(true);
                                      }}
                                    >
                                      <i className="fas fa-plus"></i>
                                      Submit Feedback
                                    </button>
                                  </div>
                                )}
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Recent Feedback */}
      <div className="recent-feedback">
        <div className="section-header">
          <h3>Recent Feedback</h3>
        </div>

        {recentFeedback.length === 0 ? (
          <div className="no-feedback">
            <i className="fas fa-comment-slash"></i>
            <p>You haven't submitted any feedback yet.</p>
            <p>Start by providing feedback on your enrolled courses above!</p>
          </div>
        ) : (
          <div className="feedback-list">
            {recentFeedback.map((feedback) => (
              <div key={feedback.id} className="feedback-item">
                <div className="feedback-header">
                  <div className="feedback-info">
                    <h4>{feedback.target_name || 'Unknown'}</h4>
                    <span className="feedback-type">{feedback.feedback_type}</span>
                    <span className="feedback-date">
                      {new Date(feedback.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <div className="feedback-rating">
                    {renderStars(feedback.rating)}
                    <span 
                      className="rating-text"
                      style={{ color: getRatingColor(feedback.rating) }}
                    >
                      {feedback.rating}/5
                    </span>
                  </div>
                </div>
                
                <div className="feedback-category">
                  <i 
                    className={getCategoryIcon(feedback.category)}
                    style={{ color: getCategoryColor(feedback.category) }}
                  ></i>
                  <span>{feedback.category}</span>
                </div>
                
                {feedback.comment && (
                  <div className="feedback-comment">
                    <p>{feedback.comment}</p>
                  </div>
                )}
                
                <div className="feedback-actions">
                  <button 
                    className="edit-btn"
                    onClick={() => {
                      setSelectedFeedback(feedback);
                      setFeedbackTarget({
                        type: feedback.feedback_type,
                        id: feedback.target_id,
                        name: feedback.target_name || 'Unknown'
                      });
                      setShowFeedback(true);
                    }}
                  >
                    <i className="fas fa-edit"></i>
                    Edit
                  </button>
                  
                  <button 
                    className="delete-btn"
                    onClick={() => handleDeleteFeedback(feedback.id)}
                  >
                    <i className="fas fa-trash"></i>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Feedback Modal */}
      {showFeedback && feedbackTarget && (
        <Feedback
          student={student}
          targetType={feedbackTarget.type}
          targetId={feedbackTarget.id}
          targetName={feedbackTarget.name}
          existingFeedback={selectedFeedback}
          onClose={() => {
            setShowFeedback(false);
            setFeedbackTarget(null);
            setSelectedFeedback(null);
            fetchFeedbackAnalytics();
            fetchRecentFeedback();
          }}
        />
      )}
    </div>
  );
};

export default FeedbackDashboard;
